mapper.select:
SELECT A.BOARD_NO AS boardNo ,C.CD_NM AS boardClass ,A.BOARD_DEV AS boardDev ,D.CD_NM AS boardNm ,A.TITLE AS title ,A.CONTENT AS content ,B.ID AS writer ,DATE_FORMAT(A.REGIST_DATE, '%Y-%m-%d') AS registDate ,DATE_FORMAT(A.UPDATE_DATE, '%Y-%m-%d') AS updateDate ,A.VIEW_CNT AS viewCnt ,COUNT(*) OVER() AS cnt FROM BOARD A JOIN USERS B ON A.WRITER = B.USER_NO JOIN COMN_CD C ON A.BOARD_CLASS = C.COMN_CD JOIN COMN_CD D ON A.BOARD_DEV = D.COMN_CD WHERE 1=1 AND DEL_YN = 1
AND BOARD_DEV = #{boardDev}
ORDER BY boardNo DESC LIMIT #{cri.pageStart}, #{cri.perPageNum}

SELECT A.BOARD_NO AS boardNo ,C.CD_NM AS boardClass ,A.TITLE AS title ,A.CONTENT AS content ,B.ID AS writer ,DATE_FORMAT(A.REGIST_DATE, '%Y-%m-%d') AS registDate ,DATE_FORMAT(A.UPDATE_DATE, '%Y-%m-%d') AS updateDate ,A.VIEW_CNT AS viewCnt ,A.BOARD_DEV AS boardDev ,COUNT(*) OVER() AS cnt FROM BOARD A JOIN USERS B ON A.WRITER = B.USER_NO JOIN COMN_CD C ON A.BOARD_CLASS = C.COMN_CD WHERE 1=1 AND A.BOARD_NO = #{boardNo}
AND BOARD_DEV = #{boardDev}
ORDER BY boardNo DESC

mapper.update:
UPDATE BOARD
   SET VIEW_CNT = (VIEW_CNT + 1)
 WHERE BOARD_NO = #{boardNo}

mapper.delete:
UPDATE BOARD
   SET DEL_YN = 0
 WHERE BOARD_NO = #{boardNo}
